import React from 'react';
import type { UnleashClient } from 'unleash-proxy-client';

export interface IFlagContextValue
  extends Pick<
    UnleashClient,
    'on' | 'off' | 'updateContext' | 'isEnabled' | 'getVariant'
  > {
  client: UnleashClient;
  flagsReady: boolean;
  setFlagsReady: React.Dispatch<
    React.SetStateAction<IFlagContextValue['flagsReady']>
  >;
  flagsError: any;
  setFlagsError: React.Dispatch<
    React.SetStateAction<IFlagContextValue['flagsError']>
  >;
}

const FlagContext = React.createContext<IFlagContextValue | null>(null);

export default FlagContext;
/** @format */

import React, { type FC, type PropsWithChildren, useEffect, useMemo, useState } from 'react';
import { type IConfig, UnleashClient } from 'unleash-proxy-client';
import FlagContext, { type IFlagContextValue } from './FlagContext';

export interface IFlagProvider {
  config?: IConfig;
  unleashClient?: UnleashClient;
  startClient?: boolean;
  stopClient?: boolean;
  startTransition?: (fn: () => void) => void;
}

const offlineConfig: IConfig = {
  bootstrap: [],
  disableRefresh: true,
  disableMetrics: true,
  url: 'http://localhost',
  appName: 'offline',
  clientKey: 'not-used',
};

// save startTransition as var to avoid webpack analysis (https://github.com/webpack/webpack/issues/14814)
const _startTransition = 'startTransition';
// fallback for React <18 which doesn't support startTransition
// Fallback for React <18 and exclude startTransition if in React Native
const defaultStartTransition = React[_startTransition] || (fn => fn());

const FlagProvider: FC<PropsWithChildren<IFlagProvider>> = ({
  config: customConfig,
  children,
  unleashClient,
  startClient = true,
  stopClient = true,
  startTransition = defaultStartTransition
}) => {
  const config = customConfig || offlineConfig;
  const client = React.useRef<UnleashClient>(
    unleashClient || new UnleashClient(config)
  );
  const [flagsReady, setFlagsReady] = React.useState(
    Boolean(
      unleashClient
        ? (customConfig?.bootstrap && customConfig?.bootstrapOverride !== false) || unleashClient.isReady?.()
        : config.bootstrap && config.bootstrapOverride !== false
    )
  );
  const [flagsError, setFlagsError] = useState(client.current.getError?.() || null);

  useEffect(() => {
    if (!config && !unleashClient) {
      console.error(
        `You must provide either a config or an unleash client to the flag provider.
        If you are initializing the client in useEffect, you can avoid this warning
        by checking if the client exists before rendering.`
      );
    }

    const errorCallback = (e: any) => {
      startTransition(() => {
        setFlagsError((currentError: any) => currentError || e);
      });
    };

    const clearErrorCallback = (e: any) => {
      startTransition(() => {
        setFlagsError(null);
      });
    }

    let timeout: ReturnType<typeof setTimeout> | null = null;
    const readyCallback = () => {
      // wait for flags to resolve after useFlag gets the same event
      timeout = setTimeout(() => {
        startTransition(() => {
          setFlagsReady(true);
        });
      }, 0);
    };

    client.current.on('ready', readyCallback);
    client.current.on('error', errorCallback);
    client.current.on('recovered', clearErrorCallback);

    if (startClient) {
      // defensively stop the client first
      client.current.stop();
      // start the client
      client.current.start();
    }

    // stop unleash client on unmount
    return function cleanup() {
      if (client.current) {
        client.current.off('error', errorCallback);
        client.current.off('ready', readyCallback);
        client.current.off('recovered', clearErrorCallback);
        if (stopClient) {
          client.current.stop();
        }
      }
      if (timeout) {
        clearTimeout(timeout);
      }
    };
  }, []);

  const context = useMemo<IFlagContextValue>(
    () => ({
      on: ((event, callback, ctx) => client.current.on(event, callback, ctx)) as IFlagContextValue['on'],
      off: ((event, callback) => client.current.off(event, callback)) as IFlagContextValue['off'],
      updateContext: async (context) => await client.current.updateContext(context),
      isEnabled: (toggleName) => client.current.isEnabled(toggleName),
      getVariant: (toggleName) => client.current.getVariant(toggleName),
      client: client.current,
      flagsReady,
      flagsError,
      setFlagsReady,
      setFlagsError,
    }),
    [flagsReady, flagsError]
  );

  return (
    <FlagContext.Provider value={context}>{children}</FlagContext.Provider>
  );
};

export default FlagProvider;
import { useContext } from "react";
import FlagContext, { type IFlagContextValue } from "./FlagContext";
import type { UnleashClient } from "unleash-proxy-client";

const methods = {
	on: (event: string, callback: Function, ctx?: any): UnleashClient => {
		console.error("on() must be used within a FlagProvider");
		return mockUnleashClient;
	},
	off: (event: string, callback?: Function): UnleashClient => {
		console.error("off() must be used within a FlagProvider");
		return mockUnleashClient;
	},
	updateContext: async () => {
		console.error("updateContext() must be used within a FlagProvider");
		return undefined;
	},
	isEnabled: () => {
		console.error("isEnabled() must be used within a FlagProvider");
		return false;
	},
	getVariant: () => {
		console.error("getVariant() must be used within a FlagProvider");
		return { name: "disabled", enabled: false };
	}
};

const mockUnleashClient = {
	...methods,
	toggles: [],
	impressionDataAll: {},
	context: {},
	storage: {},
	start: () => {},
	stop: () => {},
	isReady: () => false,
	getError: () => null,
	getAllToggles: () => []
} as unknown as UnleashClient;

const defaultContextValue: IFlagContextValue = {
	...methods,
	client: mockUnleashClient,
	flagsReady: false,
	setFlagsReady: () => {
		console.error("setFlagsReady() must be used within a FlagProvider");
	},
	flagsError: null,
	setFlagsError: () => {
		console.error("setFlagsError() must be used within a FlagProvider");
	}
};

export function useFlagContext() {
	const context = useContext(FlagContext);
	if (!context) {
		console.error("useFlagContext() must be used within a FlagProvider");
		return defaultContextValue;
	}
	return context;
}
import { useEffect, useState, useRef } from 'react';
import { useFlagContext } from './useFlagContext';

const useFlag = (featureName: string) => {
  const  { isEnabled, client }  = useFlagContext();
  const [flag, setFlag] = useState(!!isEnabled(featureName));
  const flagRef = useRef<typeof flag>();
  flagRef.current = flag;

  useEffect(() => {
    if (!client) return;

    const updateHandler = () => {
      const enabled = isEnabled(featureName);
      if (enabled !== flagRef.current) {
        flagRef.current = enabled;
        setFlag(!!enabled);
      }
    };

    const readyHandler = () => {
      const enabled = isEnabled(featureName);
      flagRef.current = enabled;
      setFlag(enabled);
    };

    client.on('update', updateHandler);
    client.on('ready', readyHandler);

    return () => {
      client.off('update', updateHandler);
      client.off('ready', readyHandler);
    };
  }, [client]);

  return flag;
};

export default useFlag;
import { useEffect, useState } from 'react';
import { useFlagContext } from './useFlagContext';

const useFlags = () => {
  const { client } = useFlagContext();
  const [flags, setFlags] = useState(client.getAllToggles());

  useEffect(() => {
    const onUpdate = () => {
      setFlags(client.getAllToggles());
    };

    client.on('update', onUpdate);

    return () => {
      client.off('update', onUpdate);
    };
  }, []);

  return flags;
};

export default useFlags;
/** @format */
import { useFlagContext } from './useFlagContext';

const useFlagsStatus = () => {
  const { flagsReady, flagsError } = useFlagContext();

  return { flagsReady, flagsError };
};

export default useFlagsStatus;
import { useState, useEffect, useRef } from 'react';
import { IVariant } from 'unleash-proxy-client';
import { useFlagContext } from './useFlagContext';

export const variantHasChanged = (
    oldVariant: IVariant,
    newVariant?: IVariant,
): boolean => {
    const variantsAreEqual =
        oldVariant.name === newVariant?.name &&
        oldVariant.enabled === newVariant?.enabled &&
        oldVariant.feature_enabled === newVariant?.feature_enabled &&
        oldVariant.payload?.type === newVariant?.payload?.type &&
        oldVariant.payload?.value === newVariant?.payload?.value;

    return !variantsAreEqual;
};

const useVariant = (featureName: string): Partial<IVariant> => {
  const { getVariant, client } = useFlagContext();

  const [variant, setVariant] = useState(getVariant(featureName));
  const variantRef = useRef<typeof variant>({
    name: variant.name,
    enabled: variant.enabled,
  });
  variantRef.current = variant;

  useEffect(() => {
    if (!client) return;

    const updateHandler = () => {
      const newVariant = getVariant(featureName);
      if (variantHasChanged(variantRef.current, newVariant)) {
        setVariant(newVariant);
        variantRef.current = newVariant;
      }
    };

    const readyHandler = () => {
      const variant = getVariant(featureName);
      variantRef.current.name = variant?.name;
      variantRef.current.enabled = variant?.enabled;
      setVariant(variant);
    };

    client.on('update', updateHandler);
    client.on('ready', readyHandler);

    return () => {
      client.off('update', updateHandler);
      client.off('ready', readyHandler);
    };
  }, [client]);

  return variant || {};
};

export default useVariant;
import { useFlagContext } from './useFlagContext';

const useUnleashContext = () => {
  const { updateContext } = useFlagContext();

  return updateContext;
};

export default useUnleashContext;
import { useFlagContext } from './useFlagContext';

const useUnleashClient = () => {
  const { client } = useFlagContext();
  return client;
};

export default useUnleashClient;
